mapper.select:
select id, password, name, email, phone, birth, reg_date
from user_info
where id = #{id} and password = #{password};

select count(*)
from user_Info
where id = #{id}

select count(*)
from user_Info
where email = #{email}

select count(*)
from user_Info
where phone = #{phone}

mapper.update:
update user_info
set password = #{password}
where id = #{id};
